var username = document.getElementById('username');
var email = document.getElementById('email');
var password1 = document.getElementById('password1');
var password2 = document.getElementById('password2');

//用户名
username.onfocus = function () {
	validateTip(username.previousElementSibling, "#666666", "用户名长度在6-19之间,由1-3位字母开头,只能包含字母、数字和下划线,请重新输入", true)
}
username.onblur = function () {
	if (/^[a-zA-Z]{1,3}\w{5,7}$/.test(this.value)) {
		validateTip(username.previousElementSibling, "green", "用户名格式正确", true)
	}else {
		validateTip(username.previousElementSibling, "red",   "用户名不符合规则,请重新输入", false)
	}
}

//邮箱
email.onfocus = function () {
	validateTip(email.previousElementSibling, "#666666", "* 请输入邮箱号", true)
}
email.onblur = function () {
	if (/^\w+([-+.]\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*$/.test(this.value)) {
		validateTip(email.previousElementSibling, "green", "邮箱格式正确", true)
	}else {
		validateTip(email.previousElementSibling, "red", "邮箱不符合规则,请重新输入", false)
	}
}


//密码
password1.onfocus = function () {
	validateTip(password1.previousElementSibling, "#666666", "* 密码长度在6~18之间，只能包含字母、数字和下划线", true)
}
password1.onblur = function () {
	if (/^[a-zA-Z0-9]\w{5,17}$/.test(this.value)) {
		validateTip(password1.previousElementSibling, "green",  "密码格式正确", true)
	}else {
		validateTip(password1.previousElementSibling, "red",  "密码不符合规则,请重新输入", false)
	}
}

//确认密码
password2.onfocus = function () {
	validateTip(password2.previousElementSibling, "#666666", "再次输入密码", true)
}
password2.onblur = function () {
	if (this.value === password1.value && this.value != "") {
		validateTip(password2.previousElementSibling, "green", "再次输入密码正确", true)
	}else {
		validateTip(password2.previousElementSibling, "red",  "第二次输入密码和第一次密码不同, 请重新输入", false)
	}
}

function pd () {
	username.onblur();
	email.onblur();
	password1.onblur();
	password2.onblur();
	let usernameflag = username.previousElementSibling.getAttribute("status");
	let emailflag= email.previousElementSibling.getAttribute("status");
	let password1flag= password1.previousElementSibling.getAttribute("status");
    let password2flag= password2.previousElementSibling.getAttribute("status");
    
	if (usernameflag == "true" && emailflag == "true" && password1flag == "true" && password2flag == "true") {
		username.value = "";
		email.value = "";
		password1.value = "";
		password2.value = "";
		alert('注册成功');
        return true;
    }else{
        username.value = "";
		email.value = "";
		password1.value = "";
		password2.value = "";
        username.previousElementSibling.innerHTML="<label></label>";
        email.previousElementSibling.innerHTML="<label></label>";
        password1.previousElementSibling.innerHTML="<label></label>";
        password2.previousElementSibling.innerHTML="<label></label>";
        alert('请按正确格式填写表单');
        return false;
    }
    
}
/**
 * 提示信息显示
 * obj:显示提示信息的元素（font）
 * css：提示样式
 * tipString:提示信息
 * status：true/false --验证是否通过
 */
