import React, { Component } from 'react';
import PubSub from 'pubsub-js';
// import Background1 from '../../../background-img/item.png';
// var sectionStyle1 = {
//     // makesure here is String确保这里是一个字符串，以下是es6写法
//     backgroundImage: `url(${Background1})`
// };
export default class Item extends Component {
    // 初始化状态
    state = {
        identity: []
    }

    //挂载完毕订阅消息
    componentDidMount() {
        this.token3 = PubSub.subscribe('showstudent', (_, stateObj) => {
            console.log(stateObj);
            this.setState(stateObj);
        });
        this.token3 = PubSub.subscribe('showteacher', (_, stateObj) => {
            console.log(stateObj);
            this.setState(stateObj);
        })
        this.token3 = PubSub.subscribe('showstudent', (_, stateObj) => {
            console.log(stateObj);
            this.setState(stateObj);
        })
    }

    //组件即将卸载取消订阅
    componentWillUnmount() {
        PubSub.unsubscribe(this.token3); //取消订阅
    }
    render() {
        const { identity } = this.props.match.params;
        const { classes } = this.state;
        const status = classes[identity];
        console.log(status);
        console.log(classes);
        return (
            <div>
                item
            </div>
        )

    }
}
