import React, { Component } from 'react';
import PubSub from 'pubsub-js';
import Background1 from '../../../background-img/item.png';
var sectionStyle1 = {
    // makesure here is String确保这里是一个字符串，以下是es6写法
    backgroundImage: `url(${Background1})`
};
export default class Item extends Component {
    // 初始化状态
    state = {
        identity: []
    }

    //挂载完毕订阅消息
    componentDidMount() {
        this.token3 = PubSub.subscribe('showstudent', (_, stateObj) => {
            console.log(stateObj);
            this.setState(stateObj);
        });
        this.token4 = PubSub.subscribe('showteacher', (_, stateObj) => {
            console.log(stateObj);
            this.setState(stateObj);
        })
        this.token5 = PubSub.subscribe('showpatriarch', (_, stateObj) => {
            console.log(stateObj);
            this.setState(stateObj);
        })
    }

    //组件即将卸载取消订阅
    componentWillUnmount() {
        PubSub.unsubscribe(this.token3); //取消订阅
        PubSub.unsubscribe(this.token4); //取消订阅
        PubSub.unsubscribe(this.token5); //取消订阅
    }
    render() {
        const { identity } = this.state;

        return (
            <div style={{ float: 'left' }}>
                {
                    identity.map((classObj) => {

                        return <div className="item" >
                            <div className="item-img" style={sectionStyle1} >
                                <span className="item-span">{classObj.identity}</span>
                            </div>
                            <div className="item-text">
                                <h2>{classObj.name}</h2>
                            </div>
                        </div>
                    })
                }
            </div>
        )

    }
}
