var school = document.getElementById('school');
var classname = document.getElementById('classname');

//用户名
school.onfocus = function () {
	validateTip(school.previousElementSibling, "#666666", "用户名长度在6-19之间,由1-3位字母开头,只能包含字母、数字和下划线,请重新输入", true)
}
school.onblur = function () {
	if (/^[a-zA-Z]{1,3}\w{5,7}$/.test(this.value)) {
		validateTip(school.previousElementSibling, "green", "用户名格式正确", true)
	}else {
		validateTip(school.previousElementSibling, "red",   "用户名不符合规则,请重新输入", false)
	}
}

//密码
classname.onfocus = function () {
	validateTip(classname.previousElementSibling, "#666666", "* 密码长度在6~18之间，只能包含字母、数字和下划线", true)
}
classname.onblur = function () {
	if (/^[a-zA-Z0-9]\w{5,17}$/.test(this.value)) {
		validateTip(classname.previousElementSibling, "green",  "密码格式正确", true)
	}else {
		validateTip(classname.previousElementSibling, "red",  "密码不符合规则,请重新输入", false)
	}
}
export function pd () {
	school.onblur();
	classname.onblur();
	let schoolflag = school.previousElementSibling.getAttribute("status");
	let classnameflag = classname.previousElementSibling.getAttribute("status");
    
	if (schoolflag === "true" && classnameflag === "true") {
		school.value = "";
		classname.value = "";
		alert('注册成功');
        return true;
    }else{
        school.value = "";
		classname.value = "";
        school.previousElementSibling.innerHTML="<label></label>";
        classname.previousElementSibling.innerHTML="<label></label>";
        alert('请按正确格式填写表单');
        return false;
    }
    
}
/**
 * 提示信息显示
 * obj:显示提示信息的元素（font）
 * css：提示样式
 * tipString:提示信息
 * status：true/false --验证是否通过
 */
function validateTip(obj, css, tipString, status) {
    obj.style.color = css;
    obj.innerHTML = tipString
    obj.setAttribute("status", status)
}
